import React from 'react'

const Header = (props) => {
  return (<h1>{props.course}</h1>)
}
const Part = (props) => {
  return (
    <p>
    {props.partN} {props.exercisesN}
  </p>)
}
const Content = (props) => {
  return (
    <div>
      <Part partN={props.partN[0].name} exercisesN={props.exercisesN[0].exercises}/>
      <Part partN={props.partN[1].name} exercisesN={props.exercisesN[1].exercises}/>
      <Part partN={props.partN[2].name} exercisesN={props.exercisesN[2].exercises}/>
    </div>
  )
}
const Total = (props) => {
  return (
    <p>Number of exercises {props.ex1 + props.ex2 + props.ex3}</p>
  )
}
const App = () => {
  const course = {
    name: 'Half Stack application development',
    parts: [
      {
        name: 'Fundamentals of React',
        exercises: 10
      },
      {
        name: 'Using props to pass data',
        exercises: 7
      },
      {
        name: 'State of a component',
        exercises: 14
      }
    ]
  }

  return (
    <div>
      <Header course={course.name}/>
      <Content partN={course.parts} exercisesN={course.parts}/>
      <Total ex1={exercises1} ex2={exercises2} ex3 ={exercises3}/>
    </div>
  )
}

export default App
